mapper.select:
select test.id as id,test.status as status,test.paper_id as paper_id,test.duration as duration,
test.start_time as start_time,test.end_time as end_time,test.name as name
from userandtest inner join test on userandtest.test_id=test.id 
where userandtest.user_id = #{userId} and test.start_time > #{currentTime}
limit #{offset},#{size};

select count(1)
from userandtest inner join test on userandtest.test_id=test.id
where userandtest.user_id = #{userId} and test.start_time > #{currentTime}

select test.id as id,test.status as status,test.paper_id as paper_id,test.duration as duration,
test.start_time as start_time,test.end_time as end_time,test.name as name
from userandtest inner join test on userandtest.test_id=test.id 
where userandtest.user_id = #{userId} and test.end_time <= #{currentTime}
limit #{offset},#{size};

select count(1)
from userandtest inner join test on userandtest.test_id=test.id
where userandtest.user_id = #{userId} and test.end_time <= #{currentTime}

select paper.question_num
from paper inner join test on paper.id=test.paper_id
where test.id = #{testId}
